tinymce.PluginManager.add('images', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('images', {
        icon: true,
        image: '/static/gutils/js/tinymce/plugins/images/tiny-image.png',
        title: 'Вставка изображения',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Вставка изображения',
                file: editor.targetElm.getAttribute('data-url-images'),
                width: 800,
                height: 482
            });
        }
    });
});
